const { src, dest } = require('gulp');
const compiler = require('webpack');
const webpack = require('webpack-stream');
const config = require('../config');
const gulpMerge = require('merge-stream');
const argv = require('yargs').argv;


export function jsCompileMin() {

  let devToolSourcemaps = argv.production ? 'none' : 'source-map';

  return gulpMerge(

    src([
      `${config.js.srcDir}/main.js`
    ])
    .pipe(webpack({
        mode: 'production',
        devtool: devToolSourcemaps,
        output: {
          filename: 'scripts.js'
        },
        watch: false,
        module: {
          rules: [
            {
              test: /\.js$/,
              loader: 'babel-loader',
              options: {
                babelrc: false,
                presets: [['@babel/preset-env']]
              }
            }
          ],
        },
  
    }, compiler, function(err, stats){
      
    }))
    .pipe(dest(`${config.js.distDir}`))
    .pipe(dest(`${config.local.appjs}`))
  )

  
}
